changeSet.sql:
CREATE TABLE users ( id uuid PRIMARY KEY, name text, surname text, email text NOT NULL, phone text NOT NULL, password text NOT NULL, user_category text NOT NULL );
CREATE TABLE menu_items ( user_id uuid REFERENCES users(id), id uuid PRIMARY KEY, name text, price numeric(17,2) );
CREATE TABLE menu_customs ( id uuid PRIMARY KEY, name text, price numeric(17,2), menu_item_id uuid REFERENCES menu_items(id) );
CREATE TABLE menu_variants( id uuid PRIMARY KEY, name text, price numeric(17,2), menu_custom_id uuid REFERENCES menu_customs(id) );

CREATE TABLE groups(
id uuid PRIMARY KEY,
group_name text NOT NULL
);
CREATE TABLE groups_users(
id uuid PRIMARY KEY,
group_id uuid REFERENCES groups(id),
user_id uuid REFERENCES users(id)
);
